#include <common/library_indicators.h>

#ifdef EXPORTLIBRARY
#define EXPORTLIBRARY something else
#error ExportLibrary was already defined
#endif

#define EXPORTLIBRARY            libmeta

#if !defined(IPLIB)
#   error IPLIB is not defined
#endif

/* Handwritten code should use META_API, not METAEXPORT */

#define META_API METAEXPORT

/* Support METAEXPORT for autogenerated schema/pif code only */

#if IPLIB==libmeta
#   if defined(__lint)
#       define METAEXPORT       __export(meta)
#       define METAGLOBAL       extern __global(meta)
#       define METAPRIVATE      extern __private(meta)
#   elif defined(_WIN32)
#       define METAEXPORT       __declspec(dllexport)
#       define METAGLOBAL       extern __declspec(dllexport)
#       define METAPRIVATE      extern
#   else
#       define METAEXPORT
#       define METAGLOBAL       extern
#       define METAPRIVATE      extern
#   endif
#else
#   if defined(__lint)
#       define METAEXPORT       __export(meta)
#       define METAGLOBAL       extern __global(meta)
#   elif defined(_WIN32) && !defined(WNT_STATIC_LINK)
#       define METAEXPORT      __declspec(dllimport)
#       define METAGLOBAL       extern __declspec(dllimport)
#   else
#       define METAEXPORT
#       define METAGLOBAL       extern
#   endif
#endif
